mapper.select:
select user.nickname,user.userId,friendrelation.note
from user,friendrelation
where user.userId in (select friendId from friendrelation where userId = #{userId} and groupname = #{groupname}) and friendrelation.userId = #{userId} and friendrelation.groupname = #{groupname}

mapper.update:
update friendgroup
set count_num=count_num+1
where userId=#{id} and groupname=#{groupname}

update friendrelation
set groupname=#{postGroupname}
where userId=#{userId} and friendId=#{friendId}

update friendgroup
set count_num=count_num-1
where groupname=#{preGroupname} and userId=#{userId}

update friendgroup
set count_num=count_num+1
where groupname=#{postGroupname} and userId=#{userId}
