mapper.select:
select user.nickname,user.userId,user.sex,user.birthday,user.address,user.phone,user.signature,friendrelation.note,friendrelation.groupname,friendrelation.evaluate
from user,friendrelation
where user.userId in (select friendId from friendrelation where userId = #{userId}) and friendrelation.userId = #{userId}

mapper.update:
update friendrelation
set groupname=#{postGroupname}
where userId=#{userId} and friendId=#{friendId}

update friendgroup
set count_num=count_num-1
where groupname=#{preGroupname} and userId=#{userId}

update friendgroup
set count_num=count_num+1
where groupname=#{postGroupname} and userId=#{userId}
